mapper.select:
select plane_id, source, target, starttime, preendtime, flight.status, economy_class, business_class, first_class, oprice,name as tName
from flight,airdrome where target=air_id

    and name like #{param}

select plane_id, source, target, starttime, preendtime, flight.status, economy_class, business_class, first_class, oprice,name as tName
from flight,airdrome where target=air_id and flight.status=3

    and name like #{param}

mapper.update:
update flight
set  preendtime = #{preendtime}, endtime = #{endtime}
where flight_id = #{flightId}

update flight
plane_id = #{planeId}, source = #{source}, target = #{target}, starttime = #{starttime}, preendtime = #{preendtime}, endtime = #{endtime}, status = #{status},
where flight_id = #{flightId}
